<?php
/**
 * footer.php — Footer component for the dashboard (enhanced with dynamic version)
 *
 * Expects a `version.js` at the site root that defines:
 *   window.appVersion = "0.0.0.<run_number>";
 */
?>
<footer class="p-4 text-center text-sm opacity-75 neumorphic">
  <div>© <?php echo date('Y'); ?> Resolutions by Design</div>
  <div class="mt-1">
    Version: <span id="appVersion">Loading…</span>
  </div>
</footer>

<!-- Load version.js generated by GitHub Actions -->
<script src="/version.js"></script>
<script>
  document.addEventListener('DOMContentLoaded', () => {
    const el = document.getElementById('appVersion');
    if (window.appVersion) {
      el.textContent = window.appVersion;
    } else {
      el.textContent = 'N/A';
    }
  });
</script>
